function base64(n){return Buffer.from(n,"ascii").toString("base64")}function unbase64(n){return Buffer.from(n,"base64").toString("ascii")}export function toRelayID(n,r){return base64([n,r].join(":"))}export function fromRelayID(n){const r=unbase64(n),t=r.indexOf(":");return{type:r.substring(0,t),id:r.substring(t+1)}}
